import React, { useState } from 'react';
import { Card, CardContent, Typography, TextField, Button, Box } from '@mui/material';

function ProjectForm() {
  const [name, setName] = useState('');
  // For simplicity, assume ownerId is 1 (in a real app, use the logged-in user's id)
  const ownerId = 1;

  const createProject = (e) => {
    e.preventDefault();
    fetch(`http://localhost:8080/api/projects?ownerId=${ownerId}&name=${encodeURIComponent(name)}`, {
      method: 'POST'
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Project created:', data);
        setName('');
      })
      .catch((error) => console.error('Error creating project:', error));
  };

  return (
    <Card sx={{ marginBottom: 4 }}>
      <CardContent>
        <Typography variant="h5" gutterBottom>
          Create New Project
        </Typography>
        <Box component="form" onSubmit={createProject} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          <TextField
            label="Project Name"
            variant="outlined"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <Button type="submit" variant="contained" color="primary">
            Create Project
          </Button>
        </Box>
      </CardContent>
    </Card>
  );
}

export default ProjectForm;
