import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Button, Grid, TextField, Box } from '@mui/material';
import { Link } from 'react-router-dom';

function ProjectList() {
  const [projects, setProjects] = useState([]);
  const [projectName, setProjectName] = useState('');

  // Fetch projects from the backend
  const fetchProjects = () => {
    const token = localStorage.getItem('jwtToken');
    fetch('http://localhost:8080/api/projects', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => res.json())
      .then((data) => setProjects(data))
      .catch((err) => console.error('Error fetching projects:', err));
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const createProject = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('jwtToken');
    try {
      const response = await fetch(`http://localhost:8080/api/projects?name=${encodeURIComponent(projectName)}`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!response.ok) {
        throw new Error('Project creation failed');
      }
      const newProject = await response.json();
      setProjectName('');
      // Refresh the projects list
      fetchProjects();
    } catch (error) {
      console.error('Error creating project:', error);
    }
  };

  return (
    <div>
      <Typography variant="h5" gutterBottom>
        Your Projects
      </Typography>
      {/* Project Creation Form */}
      <Box component="form" onSubmit={createProject} sx={{ mb: 4, display: 'flex', gap: 2 }}>
        <TextField
          label="Project Name"
          variant="outlined"
          value={projectName}
          onChange={(e) => setProjectName(e.target.value)}
          required
        />
        <Button type="submit" variant="contained" color="primary">
          Create Project
        </Button>
      </Box>
      {/* Project List */}
      {projects.length === 0 ? (
        <Typography variant="body1">No projects found. Create a project!</Typography>
      ) : (
        <Grid container spacing={2}>
          {projects.map((project) => (
            <Grid item xs={12} md={6} key={project.id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{project.name}</Typography>
                  {project.owner && (
                    <Typography variant="body2" color="text.secondary">
                      Owner: {project.owner.username}
                    </Typography>
                  )}
                  <Button
                    variant="contained"
                    color="primary"
                    component={Link}
                    to={`/projects/${project.id}`}
                    sx={{ mt: 2 }}
                  >
                    Open Project
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </div>
  );
}

export default ProjectList;
