import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Typography, Card, CardContent, Button, Box } from '@mui/material';
import TaskForm from '../Tasks/TaskForm';
import TaskList from '../Tasks/TaskList';

function ProjectDetail() {
  const { projectId } = useParams();
  const [project, setProject] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    fetch(`http://localhost:8080/api/projects/${projectId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => res.json())
      .then((data) => setProject(data))
      .catch((err) => console.error('Error fetching project details:', err));
  }, [projectId]);

  if (!project) return <Typography>Loading project details...</Typography>;

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Project: {project.name}
      </Typography>
      <Card sx={{ mb: 4 }}>
        <CardContent>
          {project.owner && (
            <Typography variant="h6">Owner: {project.owner.username}</Typography>
          )}
          {project.collaborators && project.collaborators.length > 0 && (
            <Typography variant="body1">
              Collaborators: {project.collaborators.map((c) => c.username).join(', ')}
            </Typography>
          )}
          <Button variant="contained" color="primary" sx={{ mt: 2 }}>
            Edit Project
          </Button>
        </CardContent>
      </Card>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h5" gutterBottom>
          Add Task to this Project
        </Typography>
        <TaskForm projectId={project.id} />
      </Box>
      <Box>
        <Typography variant="h5" gutterBottom>
          Tasks in this Project
        </Typography>
        <TaskList projectId={project.id} />
      </Box>
    </div>
  );
}

export default ProjectDetail;
